import React from 'react';
import Button from '../components/UI/Button/Button';
import logger from '../utils/logger';

// Create component-specific logger
const tracingLogger = logger.addContext('ButtonPropsTracing');

// Type copied from the original Button component
interface ButtonProps {
    variant?: 'primary' | 'secondary' | 'tertiary';
    size?: 'small' | 'medium' | 'large';
    themeContext?: string;
    fullWidth?: boolean;
    leftIcon?: React.ReactNode;
    rightIcon?: React.ReactNode;
    className?: string;
    children: React.ReactNode;
    onClick?: () => void;
    disabled?: boolean;
    type?: 'button' | 'submit' | 'reset';
    loading?: boolean;
    [key: string]: any; // Allow any other props
}

/**
 * Wrapper component that logs all Button props before passing them through
 */
export const TracedButton: React.FC<ButtonProps> = (props) => {
    // Log props using structured logger
    tracingLogger.group('Button Props Tracing', () => {
        tracingLogger.debug('Props received:', { ...props });

        // Analyze themeContext
        if (props.themeContext) {
            tracingLogger.debug('themeContext value:', props.themeContext);
            tracingLogger.debug('Expected class:', `button--${props.themeContext}`);
        } else {
            tracingLogger.warn('No themeContext provided');
        }

        // Log CSS class composition 
        const expectedClasses = [
            'button',
            `button--${props.variant || 'primary'}`,
            `button--${props.size || 'medium'}`,
            props.themeContext && `button--${props.themeContext}`,
            props.fullWidth && 'button--fullwidth',
            props.className
        ].filter(Boolean).join(' ');

        tracingLogger.debug('Expected final class string:', expectedClasses);
    });

    // Pass all props to the original Button
    return <Button {...props} data-traced="true" />;
};

/**
 * Demo component with various button configurations for testing
 */
export const ButtonPropsTracing: React.FC = () => {
    return (
        <div className="p-6 bg-gray-100">
            <h2 className="text-2xl font-bold mb-6">Button Props Tracing</h2>

            <div className="mb-8">
                <h3 className="text-xl font-bold mb-4">Standard Buttons</h3>
                <div className="flex flex-wrap gap-4">
                    <TracedButton variant="primary" size="medium">
                        Primary Button
                    </TracedButton>

                    <TracedButton variant="secondary" size="medium">
                        Secondary Button
                    </TracedButton>

                    <TracedButton variant="tertiary" size="medium">
                        Tertiary Button
                    </TracedButton>
                </div>
            </div>

            <div className="mb-8">
                <h3 className="text-xl font-bold mb-4">Size Variants</h3>
                <div className="flex flex-wrap gap-4">
                    <TracedButton variant="primary" size="small">
                        Small Button
                    </TracedButton>

                    <TracedButton variant="primary" size="medium">
                        Medium Button
                    </TracedButton>

                    <TracedButton variant="primary" size="large">
                        Large Button
                    </TracedButton>
                </div>
            </div>

            <div className="mb-8">
                <h3 className="text-xl font-bold mb-4">Theme Context Variants</h3>
                <div className="flex flex-wrap gap-4">
                    <TracedButton variant="primary" size="medium" themeContext="personal-training">
                        Personal Training
                    </TracedButton>

                    <TracedButton variant="primary" size="medium" themeContext="hero">
                        Hero Context
                    </TracedButton>

                    <TracedButton variant="primary" size="medium" themeContext="features">
                        Features Context
                    </TracedButton>
                </div>
            </div>

            <div className="mb-8">
                <h3 className="text-xl font-bold mb-4">Additional Props</h3>
                <div className="flex flex-wrap gap-4">
                    <TracedButton
                        variant="primary"
                        size="medium"
                        themeContext="personal-training"
                        fullWidth
                        className="additional-test-class"
                    >
                        Full Width + Custom Class
                    </TracedButton>

                    <TracedButton
                        variant="primary"
                        size="medium"
                        disabled
                    >
                        Disabled Button
                    </TracedButton>

                    <TracedButton
                        variant="primary"
                        size="medium"
                        loading
                    >
                        Loading Button
                    </TracedButton>
                </div>
            </div>

            <div>
                <p className="text-gray-700">Check the console for detailed props tracing information.</p>
            </div>
        </div>
    );
};

export default ButtonPropsTracing;

import React, { useEffect } from 'react';
import logger from '../utils/logger';

// Create component-specific logger
const classNameLogger = logger.addContext('ClassNameDebug');

/**
 * Debug class name generation to understand how different methods produce different results
 */
export const debugClassNames = (
    variant = 'primary',
    context?: string,
    size = 'medium',
    additionalClasses?: string
) => {
    // Method 1: Template literals with conditional
    const method1 = `button button--${variant} button--${size} ${context ? `button--${context}` : ''} ${additionalClasses || ''}`.trim().replace(/\s+/g, ' ');

    // Method 2: Array filter and join
    const method2 = [
        'button',
        `button--${variant}`,
        `button--${size}`,
        context && `button--${context}`,
        additionalClasses
    ].filter(Boolean).join(' ');

    // Method 3: React's classnames library simulation
    const classNames = (...classes: (string | boolean | undefined)[]) => classes.filter(Boolean).join(' ');
    const method3 = classNames(
        'button',
        `button--${variant}`,
        `button--${size}`,
        context && `button--${context}`,
        additionalClasses
    );

    return { method1, method2, method3 };
};

export const ClassNameDebug: React.FC = () => {
    useEffect(() => {
        // Test basic examples
        classNameLogger.group('Class Name Generation Tests', () => {
            classNameLogger.debug('Default (primary, medium):', debugClassNames());
            classNameLogger.debug('With context:', debugClassNames('primary', 'personal-training'));
            classNameLogger.debug('With extra classes:', debugClassNames('primary', 'personal-training', 'medium', 'my-extra-class'));
            classNameLogger.debug('With undefined context:', debugClassNames('primary', undefined, 'medium', 'my-extra-class'));
            classNameLogger.debug('With falsy context:', debugClassNames('primary', false as any, 'medium', 'my-extra-class'));
        });

        // Compare to existing button component's class name generation
        classNameLogger.group('Button Component Class Pattern Analysis', () => {
            // Example derived from Button.tsx
            const getButtonClasses = (
                variant: string = 'primary',
                size: string = 'medium',
                themeContext?: string,
                className?: string,
                fullWidth?: boolean
            ) => {
                return [
                    'button',
                    `button--${variant}`,
                    `button--${size}`,
                    themeContext && `button--${themeContext}`,
                    fullWidth && 'button--fullwidth',
                    className
                ].filter(Boolean).join(' ');
            };

            classNameLogger.debug('Button primary:', getButtonClasses('primary', 'medium'));
            classNameLogger.debug('Button personal-training:', getButtonClasses('primary', 'medium', 'personal-training'));
            classNameLogger.debug('Button full-width:', getButtonClasses('primary', 'medium', 'personal-training', '', true));
            classNameLogger.debug('Button with extra class:', getButtonClasses('primary', 'medium', 'personal-training', 'extra-class'));
        });
    }, []);

    return (
        <div className="p-6 bg-gray-100">
            <h2 className="text-2xl font-bold mb-4">Class Name Generation Analysis</h2>

            <div className="mb-8">
                <h3 className="text-xl font-bold mb-2">Current Implementation</h3>
                <pre className="bg-white p-4 rounded shadow overflow-auto">
                    {`
// From Button.tsx
const buttonClasses = [
  'button',
  \`button--\${variant}\`,
  \`button--\${size}\`,
  themeContext && \`button--\${themeContext}\`,
  fullWidth && 'button--fullwidth',
  className
].filter(Boolean).join(' ');
          `}
                </pre>
            </div>

            <div className="mb-8">
                <h3 className="text-xl font-bold mb-2">Test Cases</h3>
                <table className="w-full bg-white rounded shadow">
                    <thead>
                        <tr className="border-b">
                            <th className="p-2 text-left">Test Case</th>
                            <th className="p-2 text-left">Result</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr className="border-b">
                            <td className="p-2">Default (primary, medium)</td>
                            <td className="p-2 font-mono text-sm">{debugClassNames().method2}</td>
                        </tr>
                        <tr className="border-b">
