import { AlertCircle, Check, Loader2 } from 'lucide-react';
import React, { forwardRef, useCallback, useEffect, useRef, useState } from 'react';
import { RegistrationButton } from '../../../../components';
import AccordionSection, { AccordionSectionRef } from '../../../components/AccordionSection';
import { announceToScreenReader, focusFirstTabbableElement } from '../../utils/a11y';
import './StandardSection.scss';
import { StandardSectionProps } from './types';

/**
 * StandardSection component provides a consistent UI pattern for sections
 * in the customization journey with built-in validation, loading states,
 * error handling, and completion indicators.
 */
const StandardSection = forwardRef<AccordionSectionRef, StandardSectionProps>(
    ({
        title,
        icon,
        description,
        sectionId,
        onValidChange,
        isCompleted = false,
        onConfirm,
        className = '',
        required = false,
        error = null,
        isLoading = false,
        children,
        onOpen,
        onClose
    }, ref) => {
        const [isValid, setIsValid] = useState<boolean>(false);
        const contentRef = useRef<HTMLDivElement>(null);
        const sectionHeadingRef = useRef<HTMLDivElement>(null);

        // Report validity changes to parent
        useEffect(() => {
            onValidChange(isValid);
        }, [isValid, onValidChange]);

        // Handle section opening - focus first interactive element
        const handleOpen = useCallback(() => {
            // Announce section opening to screen readers
            announceToScreenReader(`${typeof title === 'string' ? title : 'Section'} opened`);

            // Find and focus first interactive element
            setTimeout(() => {
                focusFirstTabbableElement(contentRef.current);
            }, 100);

            if (onOpen) onOpen();
        }, [title, onOpen]);

        // Handle section closing - return focus to heading
        const handleClose = useCallback(() => {
            if (sectionHeadingRef.current) {
                sectionHeadingRef.current.focus();
            }

            if (onClose) onClose();
        }, [onClose]);

        // Create formatted section title with completion indicator
        const formattedTitle = (
            <div className="standard-section-title" ref={sectionHeadingRef}>
                {icon && <span className="section-icon">{icon}</span>}
                <span className="section-title-text">{title}</span>
                {required && (
                    <span className="section-required-badge" aria-label="Required section">
                        Required
                    </span>
                )}
                {isCompleted && (
                    <span
                        className="section-completed-badge"
                        aria-label="Section completed"
                    >
                        <Check size={12} />
                        <span>Completed</span>
                    </span>
                )}
            </div>
        );

        return (
            <div
                className={`standard-section ${className} ${isCompleted ? 'completed' : ''}`}
                id={`section-${sectionId}`}
            >
                <AccordionSection
                    title={formattedTitle}
                    ref={ref}
                    className={`standard-section-accordion ${isCompleted ? 'completed' : ''}`}
                    defaultExpanded={false}
                    onOpenStateChange={(isOpen) => isOpen ? handleOpen() : handleClose()}
                >
                    {description && (
                        <p className="section-description">{description}</p>
                    )}

                    {/* Error message */}
                    {error && (
                        <div className="section-error" role="alert">
                            <AlertCircle size={16} />
                            <span>{error}</span>
                        </div>
                    )}

                    {/* Loading indicator */}
                    {isLoading ? (
                        <div className="section-loading" aria-live="polite">
                            <Loader2 size={24} className="animate-spin" aria-hidden="true" />
                            <span>Loading...</span>
                        </div>
                    ) : (
                        <div className="section-content" ref={contentRef}>
                            {/* Render children with isValid setter as a prop */}
                            {React.Children.map(children, child => {
                                if (React.isValidElement(child)) {
                                    return React.cloneElement(child as React.ReactElement<any>, {
                                        setIsValid,
                                        isValid
                                    });
                                }
                                return child;
                            })}
                        </div>
                    )}

                    {/* Confirm button shown only if not loading and not completed */}
                    {!isLoading && !isCompleted && (
                        <div className="section-actions">
                            <RegistrationButton
                                onClick={onConfirm}
                                disabled={!isValid}
                                variant="primary"
                                size="medium"
                                rightIcon={<Check className="h-5 w-5" />}
                                className="section-confirm-btn"
                            >
                                Confirm Selection
                            </RegistrationButton>
                        </div>
                    )}
                </AccordionSection>
            </div>
        );
    }
);

// Display name for debugging
StandardSection.displayName = 'StandardSection';

export default StandardSection;

/**
 * Standardized validation helpers for CustomizedMedical sections
 */

import logger from "../../../../../utils/logger";
import { AnthropometricsData, InjuriesData, LiabilityWaiverData, MedicalClearanceData } from '../types';

/**
 * Validators for each section
 */
export const validators = {
    /**
     * Anthropometrics is valid if height and weight are provided
     */
    anthropometrics: (data: AnthropometricsData | null): boolean => {
        if (!data) return false;

        // Height validation
        const hasValidHeight = data.height && data.height.value > 0 &&
            (data.height.unit !== 'ft' || (data.height.inches !== undefined));

        // Weight validation
        const hasValidWeight = data.weight && data.weight.value > 0;

        return hasValidHeight && hasValidWeight;
    },

    /**
     * Injuries is valid if selection has been made
     */
    injuries: (data: InjuriesData | null): boolean => {
        if (!data) return false;

        // Valid if user made a selection (either has injuries or doesn't)
        const hasSelection = data.hasInjuries !== undefined;

        // If they have injuries, they should specify at least one or provide details
        if (data.hasInjuries === true) {
            return (data.selectedInjuries?.length > 0) ||
                (data.otherInjuries?.trim()?.length > 0);
        }

        return hasSelection;
    },

    /**
     * Medical clearance is valid if proper acknowledgment is provided
     */
    medicalClearance: (data: MedicalClearanceData | null): boolean => {
        if (!data) return false;

        // Valid if user acknowledged medical conditions
        const hasMedicalSelection = data.hasMedicalCondition !== undefined;

        // If they have medical conditions, they should provide some details
        if (data.hasMedicalCondition === true) {
            return !!data.medicalDetails && data.medicalDetails.trim().length > 0;
        }

        return hasMedicalSelection;
    },

    /**
     * Liability waiver is valid if accepted
     */
    liabilityWaiver: (data: LiabilityWaiverData | null): boolean => {
        if (!data) return false;

        return data.hasAcceptedWaiver === true;
    }
};

/**
 * Get validation error message for a section
 */
export const getValidationMessage = (sectionId: string): string => {
    switch (sectionId) {
        case 'anthropometrics':
            return 'Please provide your height and weight';
        case 'injuries':
            return 'Please indicate if you have any injuries';
        case 'medicalClearance':
            return 'Please provide information about any medical conditions';
        case 'liabilityWaiver':
            return 'Please accept the liability waiver to continue';
        default:
            return 'Please complete all required fields';
    }
};

/**
 * Helper to safely save data with basic retry functionality
 */
export const saveWithRetry = async <T>(
    saveFunction: (data: T) => Promise<boolean>,
    data: T,
    maxRetries = 1
): Promise<{ success: boolean; error?: string }> => {
    let attempts = 0;

    const attempt = async (): Promise<{ success: boolean; error?: string }> => {
        try {
            const result = await saveFunction(data);
            return { success: true };
        } catch (error) {
            logger.error(`Error saving data (attempt ${attempts + 1}):`, error);

            if (++attempts <= maxRetries) {
                // Simple linear retry with small delay
                await new Promise(resolve => setTimeout(resolve, 300));
                return attempt();
            } else {
                return {
                    success: false,
                    error: error instanceof Error ? error.message : "Failed to save your medical information"
                };
            }
        }
    };

    return attempt();
};

