import React, { useEffect, useRef, useState } from 'react';
import EquipmentSelector from '../CustomizeExperience/components/EquipmentSelector';
import TimeCommitmentSelector from '../CustomizeExperience/components/TimeCommitmentSelector';
import { AccordionSectionRef } from './AccordionSection';
import { useJourney } from './JourneyContext';
import { scrollToAccordionSection } from './scrollUtils';
import WorkoutPreferenceSelector from './WorkoutPreferenceSelector';

interface CustomizationContainerProps {
    onValidChange: (isValid: boolean) => void;
}

// Define section IDs for consistent reference
export const SECTION_IDS = {
    equipment: 'equipment',
    timeManagement: 'time-management',
    workoutPreference: 'workout-preference'
};

const CustomizationContainer: React.FC<CustomizationContainerProps> = ({ onValidChange }) => {
    const { registrationData, updateRegistrationData } = useJourney();

    // Track valid sections and completed sections
    // validSections will be used with the planned CompletionContext integration
    // to track validation state of each section before allowing completion
    const [validSections, setValidSections] = useState({
        [SECTION_IDS.equipment]: false,
        [SECTION_IDS.timeManagement]: false,
        [SECTION_IDS.workoutPreference]: false
    });

    const [completedSections, setCompletedSections] = useState<string[]>(
        registrationData.completedCustomizationSections || []
    );

    // Create refs for the accordion sections
    const equipmentRef = useRef<AccordionSectionRef>(null);
    const timeManagementRef = useRef<AccordionSectionRef>(null);
    const workoutPreferenceRef = useRef<AccordionSectionRef>(null);

    // Get ref by section ID
    const getSectionRef = (sectionId: string): React.RefObject<AccordionSectionRef> => {
        switch (sectionId) {
            case SECTION_IDS.equipment:
                return equipmentRef;
            case SECTION_IDS.timeManagement:
                return timeManagementRef;
            case SECTION_IDS.workoutPreference:
                return workoutPreferenceRef;
            default:
                return equipmentRef;
        }
    };

    // Get the next section ID
    const getNextSectionId = (currentSectionId: string): string | null => {
        const sections = Object.values(SECTION_IDS);
        const currentIndex = sections.indexOf(currentSectionId);
        if (currentIndex < sections.length - 1) {
            return sections[currentIndex + 1];
        }
        return null;
    };

    // Check overall validity based on section validity and completion
    useEffect(() => {
        // At least one section must be valid and completed to proceed
        const isValid = completedSections.length > 0;
        onValidChange(isValid);

        // Save completed sections to context
        updateRegistrationData({
            completedCustomizationSections: completedSections
        });
    }, [completedSections, onValidChange, updateRegistrationData]);

    // Update section validity
    const handleSectionValidChange = (section: string, isValid: boolean) => {
        setValidSections(prev => ({
            ...prev,
            [section]: isValid
        }));
    };

    // Handle confirming a section
    const handleConfirmSection = (sectionId: string) => {
        // Mark as completed if not already
        if (!completedSections.includes(sectionId)) {
            setCompletedSections(prev => [...prev, sectionId]);
        }

        // Close current section
        const currentSectionRef = getSectionRef(sectionId);
        if (currentSectionRef.current) {
            currentSectionRef.current.close();
        }

        // Open next section if available
        const nextSectionId = getNextSectionId(sectionId);
        if (nextSectionId) {
            const nextSectionRef = getSectionRef(nextSectionId);
            if (nextSectionRef.current) {
                // Use a larger delay for a smoother transition
                setTimeout(() => {
                    nextSectionRef.current?.open();

                    // Scroll to the next section with an improved offset that accounts for the header
                    // and provides more space at the top
                    setTimeout(() => {
                        // Use a smaller offset (60px) to ensure more of the section is visible
                        scrollToAccordionSection(nextSectionId, 60);
                    }, 350); // Slightly longer delay to ensure animation has started
                }, 400); // Longer delay for smoother transition between sections
            }
        }
    };

    // Initialize based on completed sections from previous session
    useEffect(() => {
        // Only open first incomplete section on mount
        const sections = Object.values(SECTION_IDS);

        // Find first incomplete section
        const firstIncompleteSection = sections.find(section =>
            !completedSections.includes(section)
        );

        // If all completed, open equipment by default
        const sectionToOpen = firstIncompleteSection || SECTION_IDS.equipment;

        // Open the appropriate section after a slight delay 
        // Use a longer delay for initial load to ensure DOM is fully ready
        setTimeout(() => {
            const sectionRef = getSectionRef(sectionToOpen);
            if (sectionRef.current) {
                sectionRef.current.open();

                // Use a more precise scrolling with a smaller offset (60px)
                // to show more content above the section
                setTimeout(() => {
                    scrollToAccordionSection(sectionToOpen, 60);
                }, 350);
            }
        }, 600); // Longer initial delay for better user experience
    }, [completedSections]);

    return (
        <div className="space-y-3">
            <p className="text-gray-300 text-sm mb-4">
                Customize your workout experience by selecting your preferences below.
                <span className="text-cyan-400 block mt-1">
                    Complete each section and click "Confirm Selection" to proceed.
                </span>
            </p>

            <div id={`accordion-section-${SECTION_IDS.equipment}`}>
                <EquipmentSelector
                    ref={equipmentRef}
                    onValidChange={(isValid) => handleSectionValidChange(SECTION_IDS.equipment, isValid)}
                    isCompleted={completedSections.includes(SECTION_IDS.equipment)}
                    onConfirm={() => handleConfirmSection(SECTION_IDS.equipment)}
                />
            </div>

            <div id={`accordion-section-${SECTION_IDS.timeManagement}`}>
                <TimeCommitmentSelector
                    ref={timeManagementRef}
                    onValidChange={(isValid) => handleSectionValidChange(SECTION_IDS.timeManagement, isValid)}
                    isCompleted={completedSections.includes(SECTION_IDS.timeManagement)}
                    onConfirm={() => handleConfirmSection(SECTION_IDS.timeManagement)}
                />
            </div>

            <div id={`accordion-section-${SECTION_IDS.workoutPreference}`}>
                <WorkoutPreferenceSelector
                    ref={workoutPreferenceRef}
                    onValidChange={(isValid) => handleSectionValidChange(SECTION_IDS.workoutPreference, isValid)}
                    isCompleted={completedSections.includes(SECTION_IDS.workoutPreference)}
                    onConfirm={() => handleConfirmSection(SECTION_IDS.workoutPreference)}
                />
            </div>

            {completedSections.length > 0 && (
                <div className="mt-4 p-3 bg-cyan-900/20 border border-cyan-800/40 rounded-lg">
                    <p className="text-cyan-300 text-sm">
                        {completedSections.length === Object.keys(SECTION_IDS).length ? (
                            "All sections completed! You can now continue to the next step."
                        ) : (
                            `${completedSections.length} of ${Object.keys(SECTION_IDS).length} sections completed. You can continue to the next step or complete more sections.`
                        )}
                    </p>
                </div>
            )}
        </div>
    );
};

export default CustomizationContainer;

import { AlertCircle, Check, Loader2 } from 'lucide-react';
import React, { forwardRef, useCallback, useEffect, useRef, useState } from 'react';
import { announceToScreenReader, focusFirstTabbableElement } from '../../CustomizeExperience/utils/a11y';
import AccordionSection, { AccordionSectionRef } from '../AccordionSection';
import './StandardSection.scss';
import { StandardSectionProps } from './types';

/**
 * StandardSection component provides a consistent UI pattern for sections
 * in the customization journey with built-in validation, loading states,
 * error handling, and completion indicators.
 */
const StandardSection = forwardRef<AccordionSectionRef, StandardSectionProps>(
    ({
        title,
        icon,
        description,
        sectionId,
        onValidChange,
        isCompleted = false,
        onConfirm,
        className = '',
        required = false,
        error = null,
        isLoading = false,
        children,
        onOpen,
        onClose
    }, ref) => {
        const [isValid, setIsValid] = useState<boolean>(false);
        const contentRef = useRef<HTMLDivElement>(null);
        const sectionHeadingRef = useRef<HTMLDivElement>(null);

        // Report validity changes to parent
        useEffect(() => {
            onValidChange(isValid);
        }, [isValid, onValidChange]);

        // Handle section opening - focus first interactive element
        const handleOpen = useCallback(() => {
            // Announce section opening to screen readers
            announceToScreenReader(`${typeof title === 'string' ? title : 'Section'} opened`);

            // Find and focus first interactive element
            setTimeout(() => {
                focusFirstTabbableElement(contentRef.current);
            }, 100);

            if (onOpen) onOpen();
        }, [title, onOpen]);

        // Handle section closing - return focus to heading
        const handleClose = useCallback(() => {
            if (sectionHeadingRef.current) {
                sectionHeadingRef.current.focus();
            }

            if (onClose) onClose();
        }, [onClose]);

        // Create formatted section title with completion indicator
        const formattedTitle = (
            <div className="standard-section-title" ref={sectionHeadingRef}>
                {icon && <span className="section-icon">{icon}</span>}
                <span className="section-title-text">{title}</span>
                {required && (
                    <span className="section-required-badge" aria-label="Required section">
                        Required
                    </span>
                )}
                {isCompleted && (
                    <span
                        className="section-completed-badge"
                        aria-label="Section completed"
                    >
                        <Check size={12} />
                        <span>Completed</span>
                    </span>
                )}
            </div>
        );

        return (
            <div
                className={`standard-section ${className} ${isCompleted ? 'completed' : ''}`}
                id={`section-${sectionId}`}
            >
                <AccordionSection
                    title={formattedTitle}
                    ref={ref}
                    className={`standard-section-accordion ${isCompleted ? 'completed' : ''}`}
                    defaultExpanded={false}
                    onOpenStateChange={(isOpen) => isOpen ? handleOpen() : handleClose()}
                >
                    {description && (
                        <p className="section-description">{description}</p>
                    )}

                    {/* Error message */}
                    {error && (
                        <div className="section-error" role="alert">
                            <AlertCircle size={16} />
                            <span>{error}</span>
                        </div>
                    )}

                    {/* Loading indicator */}
                    {isLoading ? (
                        <div className="section-loading" aria-live="polite">
                            <Loader2 size={24} className="animate-spin" aria-hidden="true" />
                            <span>Loading...</span>
                        </div>
                    ) : (
                        <div className="section-content" ref={contentRef}>
                            {/* Render children with isValid setter as a prop */}
                            {React.Children.map(children, child => {
                                if (React.isValidElement(child)) {
                                    return React.cloneElement(child as React.ReactElement<any>, {
                                        setIsValid,
                                        isValid
                                    });
                                }
                                return child;
                            })}
                        </div>
                    )}

                    {/* Confirm button shown only if not loading and not completed */}
                    {!isLoading && !isCompleted && (
                        <div className="section-actions">
                            <button
                                className={`section-confirm-btn ${isValid ? 'valid' : 'disabled'}`}
                                onClick={onConfirm}
                                disabled={!isValid}
                                aria-disabled={!isValid}
                            >
                                <Check size={16} aria-hidden="true" />
                                <span>Confirm Selection</span>
                            </button>
                        </div>
                    )}
                </AccordionSection>
            </div>
        );
    }
);

// Display name for debugging
StandardSection.displayName = 'StandardSection';

export default StandardSection;

import { X } from 'lucide-react';
import React, { useCallback, useEffect } from 'react';
import { AnimatedTransition, ProgressIndicator, RegistrationLayout } from './components';
import { useTransitionAnalytics } from './events/analyticsIntegration';
import ExperienceLevel from './ExperienceLevel';
import { useRegistrationData, useRegistrationProgress } from './hooks';
import { REGISTRATION_STEPS } from './hooks/useRegistrationProgress';
import Journey from './Journey';
import Pricing from './Pricing';
import './Registration.scss';
import Splash from './Splash';
import { RegistrationProps, RegistrationStep } from './types';

/**
 * Main Registration component that orchestrates the multi-step registration flow
 */
const Registration: React.FC<RegistrationProps> = ({
    className = '',
    initialStep = RegistrationStep.SPLASH,
    onComplete,
    onCancel
}) => {
    // Initialize analytics tracking for registration flow
    const { trackCustomEvent } = useTransitionAnalytics();

    // Get registration state and handlers from hooks
    const { data, updateData } = useRegistrationData();
    const { currentStep, nextStep, previousStep, goToStep } = useRegistrationProgress(initialStep);

    // Calculate the current step index from the current step
    const currentStepIndex = REGISTRATION_STEPS.indexOf(currentStep);

    // Track registration flow initialization
    useEffect(() => {
        trackCustomEvent('registration_flow_initiated', {
            initial_step: initialStep,
            timestamp: new Date().toISOString()
        });
    }, [trackCustomEvent, initialStep]);

    // Handle completion of the registration flow
    const handleComplete = useCallback(() => {
        // Track registration completion
        trackCustomEvent('registration_completed', {
            final_step: currentStep,
            timestamp: new Date().toISOString()
        });

        if (onComplete && data) {
            onComplete(data);
        }
    }, [onComplete, data, currentStep, trackCustomEvent]);

    // Handle cancellation of registration
    const handleCancel = useCallback(() => {
        // Track registration cancellation
        trackCustomEvent('registration_cancelled', {
            cancelled_at_step: currentStep,
            timestamp: new Date().toISOString()
        });

        if (onCancel) {
            onCancel();
        }
    }, [onCancel, currentStep, trackCustomEvent]);

    // Render the current step based on the current step value
    const renderCurrentStep = () => {
        switch (currentStep) {
            case RegistrationStep.SPLASH:
                return (
                    <Splash
                        data={data}
                        updateData={updateData}
                        onNext={nextStep}
                    />
                );

            case RegistrationStep.EXPERIENCE_LEVEL:
                return (
                    <ExperienceLevel
                        data={data}
                        updateData={updateData}
                        onNext={nextStep}
                        onBack={previousStep}
                    />
                );

            case RegistrationStep.GOALS:
            case RegistrationStep.EQUIPMENT:
            case RegistrationStep.TIME_COMMITMENT:
                return (
                    <Journey
                        data={data}
                        updateData={updateData}
                        onNext={nextStep}
                        onBack={previousStep}
                        currentStep={currentStep}
                    />
                );

            case RegistrationStep.PRICING:
                return (
                    <Pricing
                        data={data}
                        updateData={updateData}
                        onNext={nextStep}
                        onBack={previousStep}
                        onComplete={handleComplete}
                    />
                );

            // Add additional steps as needed

            default:
                return (
                    <Pricing
                        data={data}
                        updateData={updateData}
                        onNext={nextStep}
                        onBack={previousStep}
                        onComplete={handleComplete}
                    />
                );
        }
    };

    return (
        <div className={`registration ${className}`}>
            <div className="registration-header">
                <button className="registration-close" onClick={handleCancel} aria-label="Close">
                    <X size={24} />
                </button>
                <div className="registration-progress">
                    <ProgressIndicator
                        completedSections={(() => {
                            // Start with the current step and all previous steps
                            const baseCompletedSections = REGISTRATION_STEPS.slice(0, currentStepIndex + 1).map(step => step.toString());

                            // Add any additional completed sections based on the data
                            const additionalSections = [];

                            // If we have completed customization sections, mark EQUIPMENT and TIME_COMMITMENT as completed
                            if (data.completedCustomizationSections?.includes('customize_experience_completed')) {
                                additionalSections.push(RegistrationStep.EQUIPMENT.toString());
                            }

                            // Specifically check for time commitment completion
                            if (data.completedCustomizationSections?.includes('time_commitment_completed')) {
                                additionalSections.push(RegistrationStep.TIME_COMMITMENT.toString());
                            }

                            // If we have completed medical information, mark MEDICAL as completed if it exists
                            if (data.completedCustomizationSections?.includes('medical_information_completed')) {
                                // Medical doesn't have a specific step in REGISTRATION_STEPS, but we can
                                // consider it as part of the journey
                                // No op for now
                            }

                            // Combine and deduplicate
                            return [...new Set([...baseCompletedSections, ...additionalSections])];
                        })()}
                        totalSections={REGISTRATION_STEPS.length}
                        sectionLabels={REGISTRATION_STEPS.reduce((acc, step) => {
                            // Convert enum value to display name (e.g. EXPERIENCE_LEVEL -> Experience Level)
                            const displayName = step.toString()
                                .split('_')
                                .map(word => word.charAt(0).toUpperCase() + word.slice(1).toLowerCase())
                                .join(' ');
                            acc[step.toString()] = displayName;
                            return acc;
                        }, {} as Record<string, string>)}
                        variant="detailed"
                        showLabels={true}
                        accentColor="purple"
                    />
                </div>
            </div>
            <RegistrationLayout>
                <AnimatedTransition key={currentStep}>
                    {renderCurrentStep()}
                </AnimatedTransition>
            </RegistrationLayout>
        </div>
    );
};

export default Registration;

import { Check } from 'lucide-react';
import React from 'react';
import './ProgressIndicator.scss';
import { ProgressIndicatorProps } from './types';

const ProgressIndicator: React.FC<ProgressIndicatorProps> = ({
    completedSections,
    totalSections,
    sectionLabels = {},
    variant = 'default',
    showLabels = false,
    accentColor = 'purple',
    className = '',
    onSectionClick
}) => {
    // Ensure we never report more completed sections than total sections
    const validCompletedSections = completedSections.slice(0, totalSections);
    const completionCount = Math.min(validCompletedSections.length, totalSections);
    const completionPercentage = Math.min((completionCount / totalSections) * 100, 100);

    // Handle variant-specific configurations
    // _isCompact will be used in the future compact variant implementation 
    // planned in the CompletionContext integration
    const _isCompact = variant === 'compact';
    const isDetailed = variant === 'detailed';

    // Get appropriate class names based on variant and accent color
    const progressContainerClass = `progress-indicator ${variant} accent-${accentColor} ${className}`;

    return (
        <div className={progressContainerClass}>
            {showLabels && (
                <div className="progress-summary">
                    <span className="progress-label">
                        {completionCount} of {totalSections} sections complete
                    </span>
                    <span className="progress-percentage">
                        {Math.round(completionPercentage)}%
                    </span>
                </div>
            )}

            <div className="progress-bar">
                <div
                    className="progress-fill"
                    style={{ width: `${completionPercentage}%` }}
                    role="progressbar"
                    aria-valuenow={completionPercentage}
                    aria-valuemin={0}
                    aria-valuemax={100}
                />
            </div>

            {isDetailed && (
                <div className="section-indicators">
                    {[...Array(totalSections)].map((_, index) => {
                        const sectionId = Object.keys(sectionLabels)[index] || `section-${index}`;
                        const isCompleted = validCompletedSections.includes(sectionId);

                        return (
                            <div
                                key={sectionId}
                                className={`section-marker ${isCompleted ? 'completed' : ''}`}
                                onClick={() => onSectionClick && onSectionClick(sectionId)}
                                role={onSectionClick ? "button" : undefined}
                                tabIndex={onSectionClick ? 0 : undefined}
                                aria-label={sectionLabels[sectionId] || `Section ${index + 1}`}
                            >
                                {isCompleted && <Check size={12} />}
                                {showLabels && (
                                    <span className="section-label">
                                        {sectionLabels[sectionId] || `Section ${index + 1}`}
                                    </span>
                                )}
                            </div>
                        );
                    })}
                </div>
            )}
        </div>
    );
};

