/**
 * Hook to connect registration step transitions to analytics
 * Automatically tracks all step transitions with the analytics service
 */
export const useTransitionAnalytics = () => {
    useEffect(() => {
        // Subscribe to all transition events
        const unsubscribe = transitionEventManager.subscribe((event: StepTransitionEvent) => {
            // Map our event to analytics format
            analyticsService.trackEvent('registration_step_change', {
                from_step: event.sourceStep,
                to_step: event.destinationStep,
                transition_type: event.transitionType,
                timestamp: new Date(event.timestamp).toISOString(),
                duration_ms: Date.now() - event.timestamp,
                ...event.metadata
            });
        });

        // Cleanup subscription on unmount
        return () => unsubscribe();
    }, []);

    // Expose direct access to analytics service for custom events
    const trackCustomEvent = (eventName: string, properties: Record<string, any>) => {
        analyticsService.trackEvent(eventName, properties);
    };

    return { trackCustomEvent };
};

import { RegistrationStep } from '../types';
import { StepTransitionEvent, TransitionEventListener, TransitionType } from './transitionEvents';

/**
 * Manages registration step transition events and listeners
 */
class TransitionEventManager {
    private listeners: TransitionEventListener[] = [];

    /**
     * Register a listener for transition events
     * @returns Function to unsubscribe the listener
     */
    public subscribe(listener: TransitionEventListener): () => void {
        this.listeners.push(listener);

        // Return unsubscribe function
        return () => {
            this.listeners = this.listeners.filter(l => l !== listener);
        };
    }

    /**
     * Emit a transition event to all registered listeners
     */
    public emitTransition(
        sourceStep: RegistrationStep,
        destinationStep: RegistrationStep,
        transitionType: TransitionType,
        metadata?: Record<string, any>
    ): void {
        const event: StepTransitionEvent = {
            sourceStep,
            destinationStep,
            transitionType,
            timestamp: Date.now(),
            metadata
        };

        // Notify all listeners
        this.listeners.forEach(listener => listener(event));

        // Log for debugging
        logger.debug(
            `[Registration Event] ${sourceStep}  ${destinationStep} (${transitionType})`,
            metadata ? metadata : ''
        );
    }
}

// Singleton instance
export const transitionEventManager = new TransitionEventManager();

import { RegistrationStep } from '../types';

/**
 * Types of transitions between registration steps
 */
export enum TransitionType {
    STANDARD = 'standard',     // Regular next step in sequence
    MAP_BASED = 'map_based',   // Using STEP_TRANSITION_MAP for non-sequential transitions
    DIRECT = 'direct',         // Using goToStep for direct navigation
    BACK = 'back',             // Going backward in flow
    OVERRIDE = 'override'      // System override (like forced navigation)
}

/**
 * Event data structure for step transitions
 */
export interface StepTransitionEvent {
    sourceStep: RegistrationStep;
    destinationStep: RegistrationStep;
    transitionType: TransitionType;
    timestamp: number;
    metadata?: Record<string, any>;
}

/**
 * Listener function type for transition events
 */
export type TransitionEventListener = (event: StepTransitionEvent) => void;

import { useCallback } from 'react';
import { transitionEventManager } from '../events/transitionEventManager';
import { TransitionEventListener, TransitionType } from '../events/transitionEvents';
import { RegistrationStep } from '../types';

/**
 * Hook for working with registration transition events
 * Provides methods to track different types of step transitions
 */
export const useRegistrationEvents = () => {
    // Subscribe to transition events
    const subscribeToTransitions = useCallback(
        (listener: TransitionEventListener) => {
            return transitionEventManager.subscribe(listener);
        },
        []
    );

    // Track standard transition (next in sequence)
    const trackStandardTransition = useCallback(
        (sourceStep: RegistrationStep, destinationStep: RegistrationStep, metadata?: Record<string, any>) => {
            transitionEventManager.emitTransition(
                sourceStep,
                destinationStep,
                TransitionType.STANDARD,
                metadata
            );
        },
        []
    );

    // Track map-based transition (using transition map)
    const trackMapTransition = useCallback(
        (sourceStep: RegistrationStep, destinationStep: RegistrationStep, metadata?: Record<string, any>) => {
            transitionEventManager.emitTransition(
                sourceStep,
                destinationStep,
                TransitionType.MAP_BASED,
                metadata
            );
        },
        []
    );

    // Track direct navigation (goToStep)
    const trackDirectNavigation = useCallback(
        (sourceStep: RegistrationStep, destinationStep: RegistrationStep, metadata?: Record<string, any>) => {
            transitionEventManager.emitTransition(
                sourceStep,
                destinationStep,
                TransitionType.DIRECT,
                metadata
            );
        },
        []
    );

    // Track back navigation
    const trackBackNavigation = useCallback(
        (sourceStep: RegistrationStep, destinationStep: RegistrationStep, metadata?: Record<string, any>) => {
            transitionEventManager.emitTransition(
                sourceStep,
                destinationStep,
                TransitionType.BACK,
                metadata
            );
        },
        []
    );

    // Track override navigation
    const trackOverrideNavigation = useCallback(
        (sourceStep: RegistrationStep, destinationStep: RegistrationStep, metadata?: Record<string, any>) => {
            transitionEventManager.emitTransition(
                sourceStep,
                destinationStep,
                TransitionType.OVERRIDE,
                metadata
            );
        },
        []
    );

    return {
        subscribeToTransitions,
        trackStandardTransition,
        trackMapTransition,
        trackDirectNavigation,
        trackBackNavigation,
        trackOverrideNavigation
    };
};

