import React, { useEffect, useRef } from 'react';
import logger from './logger';

declare global {
    interface Window {
        fitcopilotDebugTools?: {
            logRender: (componentName: string, renderTime: number) => void;
            getComponentStats: () => {
                total: number;
                rendered: number;
