/**
 * Function to check if debug mode is active
 */
export const isDebugMode = (): boolean => {
    const urlParams = new URLSearchParams(window.location.search);
    return urlParams.get('show_react_debug') === 'true';
};

/**
 * Log a message to the debug console
 * 
 * @param message The message to log
 * @param type The type of message (log, warn, error)
 */
export const debugLog = (message: string, type: 'log' | 'warn' | 'error' = 'log'): void => {
    if (!isDebugMode()) return;

    const styles = {
        log: 'background: #1f2937; color: #a3e635; padding: 2px 4px;',
        warn: 'background: #fbbf24; color: #1f2937; padding: 2px 4px;',
        error: 'background: #ef4444; color: white; padding: 2px 4px;'
    };

    // Use the logger utility based on the type
    if (type === 'log') {
        logger.debug(`FitCopilot Debug: ${message}`);
    } else if (type === 'warn') {
        logger.warn(`FitCopilot Debug: ${message}`);
    } else if (type === 'error') {
        logger.error(`FitCopilot Debug: ${message}`);
    }
};

/**
 * Measure component render time
 * 
 * @param componentName The name of the component to measure
 */
export const useRenderTime = (componentName: string): void => {
    const startTime = useRef<number>(performance.now());

    useEffect(() => {
        if (!isDebugMode()) return;

        const endTime = performance.now();
        const renderTime = Math.round(endTime - startTime.current);

        if (window.fitcopilotDebugTools?.logRender) {
            window.fitcopilotDebugTools.logRender(componentName, renderTime);
        } else {
            debugLog(`${componentName} rendered in ${renderTime}ms`, 'log');
        }

        return () => {
            debugLog(`${componentName} unmounted`, 'log');
        };
    }, [componentName]);
};

/**
 * Component wrapper for adding debugging functionality to a component
 */
export const withDebug = <P extends object>(
    WrappedComponent: React.ComponentType<P>,
    componentName?: string
): React.FC<P> => {
    const name = componentName || WrappedComponent.displayName || WrappedComponent.name || 'UnknownComponent';

    const ComponentWithDebug: React.FC<P> = (props) => {
        useRenderTime(name);

        if (!isDebugMode()) {
            return <WrappedComponent {...props} />;
        }

        return (
            <>
                <div className="react-debug-component" data-component={name} id={`react-debug-${name.toLowerCase().replace(/[^a-z0-9]/g, '-')}`}>
                    <span className="react-debug-component-name">{name}</span>
                    <span className="react-debug-render-time"></span>
                </div>
                <WrappedComponent {...props} />
            </>
        );
    };

    ComponentWithDebug.displayName = `Debug(${name})`;

    return ComponentWithDebug;
};

/**
 * React hook for debugging prop changes in a component
 * 
 * @param props The props object to monitor
 * @param componentName The name of the component
 */
export const useDebugProps = <P extends object>(props: P, componentName: string): void => {
    const prevProps = useRef<P | null>(null);

    useEffect(() => {
        if (!isDebugMode()) return;

        if (prevProps.current) {
            const changes: Record<string, { from: any, to: any }> = {};
            let hasChanges = false;

            Object.keys(props).forEach((key) => {
                const propKey = key as keyof P;
                if (props[propKey] !== prevProps.current![propKey]) {
                    changes[key] = {
                        from: prevProps.current![propKey],
                        to: props[propKey]
                    };
                    hasChanges = true;
                }
            });

            if (hasChanges) {
                debugLog(`${componentName} props changed:`, 'log');
                logger.group('Prop Changes', () => {
                    logger.debug('Changed props:', changes);
                });
            }
        }

        prevProps.current = { ...props };
    }, [props, componentName]);
};

/**
 * Performance monitor component
 */
export const PerformanceMonitor: React.FC = () => {
    const frameRef = useRef<number>(0);
    const fpsRef = useRef<number[]>([]);
    const memoryRef = useRef<HTMLDivElement>(null);
    const fpsCounterRef = useRef<HTMLDivElement>(null);

    useEffect(() => {
        if (!isDebugMode()) return;

        let lastTime = performance.now();
        let frames = 0;

        const loop = () => {
            const now = performance.now();
            frames++;

            if (now >= lastTime + 1000) {
                const fps = Math.round((frames * 1000) / (now - lastTime));
                fpsRef.current.push(fps);

                if (fpsRef.current.length > 60) {
                    fpsRef.current.shift();
                }

                const avgFps = Math.round(fpsRef.current.reduce((sum, val) => sum + val, 0) / fpsRef.current.length);

                if (fpsCounterRef.current) {
                    fpsCounterRef.current.textContent = `${avgFps} FPS`;

                    if (avgFps < 30) {
                        fpsCounterRef.current.style.color = '#ef4444';
                    } else if (avgFps < 50) {
                        fpsCounterRef.current.style.color = '#fbbf24';
                    } else {
                        fpsCounterRef.current.style.color = '#a3e635';
                    }
                }

                frames = 0;
                lastTime = now;
            }

            // Update memory usage if available
            if (memoryRef.current && (performance as any).memory) {
                const memory = (performance as any).memory;
                const usedMemory = Math.round(memory.usedJSHeapSize / (1024 * 1024));
                const totalMemory = Math.round(memory.jsHeapSizeLimit / (1024 * 1024));

                memoryRef.current.textContent = `Memory: ${usedMemory}MB / ${totalMemory}MB`;

                const memoryUsage = usedMemory / totalMemory;
                if (memoryUsage > 0.8) {
                    memoryRef.current.style.color = '#ef4444';
                } else if (memoryUsage > 0.5) {
                    memoryRef.current.style.color = '#fbbf24';
                } else {
                    memoryRef.current.style.color = '#a3e635';
                }
            }

            frameRef.current = requestAnimationFrame(loop);
        };

        frameRef.current = requestAnimationFrame(loop);

        return () => {
            cancelAnimationFrame(frameRef.current);
        };
    }, []);

    if (!isDebugMode()) {
        return null;
    }

    return (
        <div
            style={{
                position: 'fixed',
                top: '0',
                right: '0',
                background: 'rgba(0, 0, 0, 0.7)',
                color: '#fff',
                padding: '5px 10px',
                fontSize: '12px',
                fontFamily: 'monospace',
                zIndex: 9999,
                display: 'flex',
                flexDirection: 'column',
                gap: '4px'
            }}
        >
            <div ref={fpsCounterRef}>-- FPS</div>
            <div ref={memoryRef}>Memory: --MB / --MB</div>
        </div>
    );
};

export default {
    isDebugMode,
    debugLog,
    useRenderTime,
    withDebug,
    useDebugProps,
    PerformanceMonitor
};

import React, { lazy, Suspense } from 'react';

interface LazyLoadOptions {
    /**
     * Custom fallback component to show while loading
     * @default default skeleton loader
     */
    fallback?: React.ReactNode;

    /**
     * Error boundary fallback to show on error
     * @default generic error UI
     */
    errorFallback?: React.ComponentType<{ error: Error; retry: () => void }>;

    /**
     * Minimum delay before showing component (prevents flash)
     * @default 0
     */
    delay?: number;

    /**
     * Whether to prefetch the component
     * @default false
     */
    prefetch?: boolean;
}

/**
 * Default skeleton loader for lazy loaded components
 */
const DefaultSkeleton: React.FC<{ className?: string }> = ({ className = '' }) => (
    <div
        className={`lazy-loading-skeleton ${className}`}
        role="status"
        aria-busy="true"
        aria-live="polite"
    >
        <div className="skeleton-animation"></div>
        <span className="visually-hidden">Loading...</span>
    </div>
);

/**
 * Default error fallback for lazy loaded components
 */
const DefaultErrorFallback: React.FC<{ error: Error; retry: () => void }> = ({ error, retry }) => (
    <div className="lazy-loading-error" role="alert">
        <h3>Something went wrong</h3>
        <p>{error.message || 'Failed to load component'}</p>
        <button onClick={retry} className="retry-button">
            Try Again
        </button>
    </div>
);

/**
 * Error boundary component for handling lazy loading errors
 */
class ErrorBoundary extends React.Component<
    {
        children: React.ReactNode;
        fallback: React.ComponentType<{ error: Error; retry: () => void }>;
    },
    { hasError: boolean; error: Error | null }
> {
    constructor(props: { children: React.ReactNode; fallback: React.ComponentType<{ error: Error; retry: () => void }> }) {
        super(props);
        this.state = { hasError: false, error: null };
    }

    static getDerivedStateFromError(error: Error) {
        return { hasError: true, error };
    }

    handleRetry = () => {
        this.setState({ hasError: false, error: null });
    };

    render() {
        const { hasError, error } = this.state;
        const { children, fallback: Fallback } = this.props;

        if (hasError && error) {
            return <Fallback error={error} retry={this.handleRetry} />;
        }

        return children;
    }
}

/**
 * Creates a lazy-loaded component with error boundary and custom fallback
 * 
 * @param factory Function that imports the component
 * @param options Configuration options for lazy loading
 * @returns Lazy-loaded component with error handling
 * 
 * @example
 * // Basic usage
 * const LazyHero = lazyLoad(() => import('../features/Homepage/Hero/Hero'));
 * 
 * @example
 * // With custom fallback
 * const LazyTraining = lazyLoad(
 *   () => import('../features/Homepage/Training/Training'),
 *   { 
 *     fallback: <TrainingSkeleton />,
 *     prefetch: true
 *   }
 * );
 */
export function lazyLoad<T extends React.ComponentType<any>>(
    factory: () => Promise<{ default: T }>,
    options: LazyLoadOptions = {}
): React.ComponentType<React.ComponentProps<T>> {
    const {
        fallback = <DefaultSkeleton />,
        errorFallback = DefaultErrorFallback,
        delay = 0,
        prefetch = false
    } = options;

    // Create the lazy component
    const LazyComponent = lazy(() => {
        if (delay > 0) {
            return new Promise(resolve => {
                setTimeout(() => {
                    resolve(factory());
                }, delay);
            });
        }
        return factory();
    });

    // Handle prefetching
    if (prefetch) {
        // Prefetch in the next idle period
        window.requestIdleCallback?.(() => {
            factory().catch(() => {
                // Silent prefetch error
            });
        }, { timeout: 2000 });
    }

    // Return wrapped component
    const WrappedLazyComponent = (props: React.ComponentProps<T>) => (
        <ErrorBoundary fallback={errorFallback}>
            <Suspense fallback={fallback}>
                <LazyComponent {...props} />
            </Suspense>
        </ErrorBoundary>
    );

    // Add display name to fix the linting error
    WrappedLazyComponent.displayName = `LazyLoaded(${factory.toString().split('(')[0].split(' ').pop() || 'Component'})`;

    return WrappedLazyComponent;
}

/**
 * Creates a variant-aware lazy loaded component that only loads the selected variant
 * 
 * @param variantMap Map of variant keys to component import factories
 * @param options Configuration options for lazy loading
 * @returns A component that will dynamically load the selected variant
 * 
 * @example
 * const LazyFeatures = lazyLoadVariant({
 *   default: () => import('../features/Homepage/Features/variants/default'),
 *   sports: () => import('../features/Homepage/Features/variants/sports'),
 *   wellness: () => import('../features/Homepage/Features/variants/wellness')
 * });
 * 
 * // Later in JSX:
 * <LazyFeatures variant="sports" />
 */
export function lazyLoadVariant<V extends string, P extends { variant?: V }>(
    variantMap: Record<string, () => Promise<{ default: React.ComponentType<P> }>>,
    options: LazyLoadOptions = {}
): React.ComponentType<P> {
    const lazyVariants: Record<string, React.ComponentType<P>> = {};

    // Create lazy component for each variant
    Object.entries(variantMap).forEach(([variant, factory]) => {
        lazyVariants[variant] = lazyLoad(factory, options);
    });

    // Return component that renders the appropriate variant
    const VariantComponent = function (props: P) {
        const variant = (props.variant || 'default') as string;
        const Component = lazyVariants[variant] || lazyVariants['default'];

        return <Component {...props} />;
    };

    // Add display name to fix the linting error
    VariantComponent.displayName = 'LazyLoadedVariant';

    return VariantComponent;
}

export default lazyLoad;

/**
 * Log level definitions
 * Separated to avoid circular dependencies and initialization issues
 */

// Log levels enum - IMPORTANT: This must be first in the file
export enum LogLevel {
    DEBUG = 0,
    INFO = 1,
    WARN = 2,
    ERROR = 3,
}

// Safe fallback values in case enum isn't initialized
export const LOG_LEVEL_VALUES = {
    DEBUG: 0,
    INFO: 1,
    WARN: 2,
    ERROR: 3
};

// Helper function for safely accessing log levels even if enum initialization fails
export const getLogLevelValue = (level: keyof typeof LogLevel): number => {
    try {
        // Try to access through the enum first
        return LogLevel[level];
    } catch (_e) {
        // Fallback to hardcoded values if enum access fails
        return LOG_LEVEL_VALUES[level] || LOG_LEVEL_VALUES.INFO;
    }
};

/**
 * Logger utility for consistent logging across the application
 * Provides environment-aware logging and support for different log levels
 */

import { LOG_LEVEL_VALUES, LogLevel } from './logLevels';

// Determine environment
const isProduction = process.env.NODE_ENV === 'production';
// const isDevelopment = process.env.NODE_ENV === 'development'; // Removed unused variable
const isTest = process.env.NODE_ENV === 'test';

// Advanced configuration with environment-specific settings
interface LoggerConfig {
    minLevel: number; // Changed from LogLevel to number for safety
    enableConsole: boolean;
    enableRemoteLogging?: boolean;
    enableGrouping: boolean;
    enableTimers: boolean;
}

// Safely access log level values
const safeLogLevel = (level: keyof typeof LogLevel): number => {
    try {
        return LogLevel[level];
    } catch (e) {
        // Fallback to hardcoded values if enum access fails
        return LOG_LEVEL_VALUES[level];
    }
};

// Default configurations based on environment - use safe access
const DEFAULT_CONFIGS: Record<string, LoggerConfig> = {
    production: {
        minLevel: safeLogLevel('WARN'),
        enableConsole: true,
        enableRemoteLogging: true,
        enableGrouping: false,
        enableTimers: false
    },
    development: {
        minLevel: safeLogLevel('DEBUG'),
        enableConsole: true,
        enableRemoteLogging: false,
        enableGrouping: true,
        enableTimers: true
    },
    test: {
        minLevel: safeLogLevel('ERROR'),
        enableConsole: false,
        enableRemoteLogging: false,
        enableGrouping: false,
        enableTimers: false
    }
};

// Re-export LogLevel
export { LogLevel };

// Get environment-specific default config
const getDefaultConfig = (): LoggerConfig => {
    if (isProduction) return DEFAULT_CONFIGS.production;
    if (isTest) return DEFAULT_CONFIGS.test;
    return DEFAULT_CONFIGS.development;
};

// Active configuration (starts with environment default)
let activeConfig = getDefaultConfig();

// Function to set or update logger configuration
export const configureLogger = (config: Partial<LoggerConfig>): void => {
    activeConfig = { ...activeConfig, ...config };
};

// Timer IDs for performance measurement
const timers: Record<string, number> = {};
let timerCounter = 0;

// Safe console methods with fallbacks
const safeConsole = {
    log: (...args: unknown[]) => {
        try {
            // eslint-disable-next-line no-console
            console.log(...args);
        } catch (_e) {
            // Silent fallback if console is not available
        }
    },
    info: (...args: unknown[]) => {
        try {
            // eslint-disable-next-line no-console
            console.info(...args);
        } catch (_e) {
            // Silent fallback if console is not available
        }
    },
    warn: (...args: unknown[]) => {
        try {
            // eslint-disable-next-line no-console
            console.warn(...args);
        } catch (_e) {
            // Silent fallback if console is not available
        }
    },
    error: (...args: unknown[]) => {
        try {
            // eslint-disable-next-line no-console
            console.error(...args);
        } catch (_e) {
            // Silent fallback if console is not available
        }
    },
    group: (label: string) => {
        try {
            // eslint-disable-next-line no-console
            console.group(label);
        } catch (_e) {
            // Silent fallback if console is not available
        }
    },
    groupEnd: () => {
        try {
            // eslint-disable-next-line no-console
            console.groupEnd();
        } catch (_e) {
            // Silent fallback if console is not available
        }
    },
    time: (label: string) => {
        try {
            // eslint-disable-next-line no-console
            console.time(label);
        } catch (_e) {
            // Silent fallback if console is not available
        }
    },
    timeEnd: (label: string) => {
        try {
            // eslint-disable-next-line no-console
            console.timeEnd(label);
        } catch (_e) {
            // Silent fallback if console is not available
        }
    }
};

/**
 * Base logging function
 * @param level Log level
 * @param message Main message to log
 * @param data Additional data to log
 */
const logMessage = (level: number, message: string, ...data: unknown[]): void => {
    // Skip logging if the level is below the configured minimum level
    if (level < activeConfig.minLevel) return;

    // Format the message with a timestamp
    const timestamp = new Date().toISOString();
    const formattedMessage = `[${timestamp}] ${message}`;

    // Skip console output if disabled in config
    if (!activeConfig.enableConsole) return;

    // Use direct console access with safety checks
    try {
        // Log to console based on level
        switch (level) {
            case safeLogLevel('DEBUG'):
                safeConsole.log(formattedMessage, ...data);
                break;
            case safeLogLevel('INFO'):
                safeConsole.info(formattedMessage, ...data);
                break;
            case safeLogLevel('WARN'):
                safeConsole.warn(formattedMessage, ...data);
                break;
            case safeLogLevel('ERROR'):
                safeConsole.error(formattedMessage, ...data);
                break;
        }
    } catch (_e) {
        // Silent fallback if console access fails
    }

    // In production with remote logging enabled, send logs to remote service
    if (isProduction && activeConfig.enableRemoteLogging) {
        // TODO: Implement remote logging service integration
        // Example: sendLogToRemoteService(level, message, data);
    }
};

/**
 * Log debug message
 * Only visible in development mode or when log level is set to DEBUG
 */
export const debug = (message: string, ...data: unknown[]): void => {
    logMessage(safeLogLevel('DEBUG'), message, ...data);
};

/**
 * Log info message
 * General information about application flow
 */
export const info = (message: string, ...data: unknown[]): void => {
    logMessage(safeLogLevel('INFO'), message, ...data);
};

/**
 * Log warning message
 * For issues that don't prevent the application from functioning
 */
export const warn = (message: string, ...data: unknown[]): void => {
    logMessage(safeLogLevel('WARN'), message, ...data);
};

/**
 * Log error message
 * For issues that affect functionality
 */
export const error = (message: string, ...data: unknown[]): void => {
    logMessage(safeLogLevel('ERROR'), message, ...data);
};

/**
 * Capture and log error objects
 * Useful for try/catch blocks
 */
export const captureError = (err: unknown, context: Record<string, unknown> = {}): void => {
    const errorMessage = err instanceof Error
        ? err.message
        : (typeof err === 'string' ? err : 'Unknown error');

    const component = context.component ? `[${String(context.component)}] ` : '';
    const contextMessage = `${component}${errorMessage}`;

    error(contextMessage, { ...context, error: err });

    // In production, we might want to send errors to an error tracking service
    if (isProduction) {
        // TODO: Implement error tracking service integration
        // Example: Sentry, LogRocket, etc.
    }
};

/**
 * Group related logs together
 */
export const group = (label: string, callback: () => void): void => {
    if (!activeConfig.enableGrouping) {
        // Just execute the callback without grouping if grouping is disabled
        callback();
        return;
    }

    try {
        // eslint-disable-next-line no-console
        safeConsole.group(label);
        callback();
        // eslint-disable-next-line no-console
        safeConsole.groupEnd();
    } catch (e) {
        // If grouping fails, just run the callback
        callback();
    }
};

/**
 * Start timing a performance measurement
 * @param label Label for the timer
 * @returns Timer ID to use with timeEnd
 */
export const time = (label: string): string => {
    const timerId = `${label}-${timerCounter++}`;
    if (!activeConfig.enableTimers) return timerId;

    if (!isProduction) {
        try {
            // eslint-disable-next-line no-console
            safeConsole.time(timerId);
        } catch (e) {
            // Silent fallback if console.time is not available
        }
    }

    try {
        timers[timerId] = performance.now();
    } catch (e) {
        // Silent fallback if performance API is not available
    }

    return timerId;
};

/**
 * End timing a performance measurement
 * @param timerId Timer ID from time()
 */
export const timeEnd = (timerId: string): void => {
    if (!timers[timerId]) {
        try {
            warn(`Timer '${timerId}' does not exist`);
        } catch (e) {
            // Silent fallback
        }
        return;
    }

    try {
        const elapsed = performance.now() - timers[timerId];

        if (!activeConfig.enableTimers) return;

        if (!isProduction) {
            try {
                // eslint-disable-next-line no-console
                safeConsole.timeEnd(timerId);
            } catch (e) {
                // Silent fallback if console.timeEnd is not available
            }
        }

        delete timers[timerId];

        try {
            debug(`Operation took ${elapsed.toFixed(2)}ms`, { timerId, duration: elapsed });
        } catch (e) {
            // Silent fallback
        }
    } catch (e) {
        // Silent fallback if performance API is not available
    }
};

/**
 * Create a component-specific logger
 * @param component Component name or identifier
 * @returns Logger with component context
 */
export const addContext = (component: string) => {
    return {
        debug: (msg: string, ...args: unknown[]) => debug(`[${component}] ${msg}`, ...args),
        info: (msg: string, ...args: unknown[]) => info(`[${component}] ${msg}`, ...args),
        warn: (msg: string, ...args: unknown[]) => warn(`[${component}] ${msg}`, ...args),
        error: (msg: string, ...args: unknown[]) => error(`[${component}] ${msg}`, ...args),
        captureError: (err: unknown, context: Record<string, unknown> = {}) =>
            captureError(err, { ...context, component }),
        time: (label: string) => time(`${component}:${label}`),
        timeEnd,
        group: (label: string, callback: () => void) => group(`${component}: ${label}`, callback),
    };
};

// Function to set the current log level (maintained for backwards compatibility)
export const setLogLevel = (level: number): void => {
    configureLogger({ minLevel: level });
};

// Create the logger object
const logger = {
    debug,
    info,
    warn,
    error,
    captureError,
    group,
    time,
    timeEnd,
    addContext,
    setLogLevel,
    configureLogger,
    LogLevel // Re-export for compatibility
};

// Make logger globally available as a fallback
if (typeof window !== 'undefined') {
    try {
        // @ts-expect-error - Explicitly adding to window
        window.logger = logger;
    } catch (e) {
        // Silent fallback if window is not available or cannot be modified
    }
}

// Default export for normal import usage
export default logger;

import React from 'react';
import logger from './logger';

/**
 * Enhanced Variant Loader Utility
 * 
 * This utility provides dynamic loading of component variants based on the theme
 * setting or user preferences. It uses React's lazy loading to reduce initial
 * bundle size by dynamically importing variant components when needed.
 */

// Type definitions for variant components
export type VariantKey = 'default' | 'modern' | 'wellness' | 'sports' | 'boutique' | 'classic' | 'minimalist' | 'registration';

// Map of variant to component path (for dynamic imports)
const variantPaths: Record<string, string> = {
    'default': 'default',
    'modern': 'modern',
    'wellness': 'wellness',
    'sports': 'sports',
    'boutique': 'boutique',
    'classic': 'classic',
    'minimalist': 'minimalist',
    'registration': 'registration'
};

// Cache for loaded variant components
const variantCache: Record<string, Record<VariantKey, any>> = {};

/**
 * Dynamic variant importer function
 * 
 * @param featurePath Path to the feature (e.g., 'Homepage/Hero')
 * @param variant Variant key to load
 * @returns Promise that resolves to the variant component
 */
export const importVariant = async (featurePath: string, variant: VariantKey = 'default') => {
    try {
        // Use actual variant path or fallback to default
        const variantPathKey = variantPaths[variant] || 'default';

        // Dynamic import based on feature path and variant
        // Use a more specific path to avoid webpack trying to load all files
        const module = await import(`../features/${featurePath}/variants/${variantPathKey}/index.ts`);

        // Return the component or fallback to default export
        return module.default || module;
    } catch (err) {
        logger.error(`Error loading variant ${variant} for ${featurePath}:`, err);

        // Fallback to default variant
        if (variant !== 'default') {
            logger.info(`Falling back to default variant for ${featurePath}`);
            return importVariant(featurePath, 'default');
        }

        // If even the default fails, throw the error
        throw err;
    }
};

/**
 * Gets the appropriate component variant based on the current theme
 * 
 * @param variantMap Map of available variants
 * @param defaultVariant Default variant to use if none matches
 * @returns The appropriate variant component
 */
export const getComponentVariant = (
    variantMap: Record<VariantKey, React.ComponentType<any>>,
    defaultVariant: React.ComponentType<any>
): React.ComponentType<any> => {
    // Determine which variant to use from data-theme attribute or default
    const bodyTheme = document.body.getAttribute('data-theme') as VariantKey;

    // Check if variant exists in map
    if (bodyTheme && variantMap[bodyTheme]) {
        return variantMap[bodyTheme];
    }

    // Return default variant
    return defaultVariant || variantMap.default || variantMap.modern;
};

/**
 * Creates a React component that dynamically loads the appropriate variant
 * 
 * @param featurePath Path to the feature (e.g., 'Homepage/Hero')
 * @returns A component that will render the appropriate variant
 */
export const createVariantComponent = (featurePath: string) => {
    // Create a React component
    const VariantLoader = (props: any) => {
        // Determine which variant to use (from data-theme attribute or props)
        const [variant, setVariant] = React.useState<VariantKey>('default');
        const [VariantComponent, setVariantComponent] = React.useState<React.ComponentType<any> | null>(null);
        const [isLoading, setIsLoading] = React.useState(true);
        const [error, setError] = React.useState<Error | null>(null);

        // Effect to determine and load the variant
        React.useEffect(() => {
            const determineVariant = () => {
                // First check props for variant
                if (props.variant && variantPaths[props.variant]) {
                    return props.variant as VariantKey;
                }

                // Then check for body data-theme attribute
                const bodyTheme = document.body.getAttribute('data-theme');
                if (bodyTheme && variantPaths[bodyTheme]) {
                    return bodyTheme as VariantKey;
                }

                // Default fallback
                return 'default' as VariantKey;
            };

            const selectedVariant = determineVariant();
            setVariant(selectedVariant);

            // Load the variant if not already cached
            if (!variantCache[featurePath]) {
                variantCache[featurePath] = {} as Record<VariantKey, any>;
            }

            // If we have the component in cache, use it
            if (variantCache[featurePath][selectedVariant]) {
                setVariantComponent(variantCache[featurePath][selectedVariant]);
                setIsLoading(false);
                return;
            }

            // Otherwise, dynamically import it
            setIsLoading(true);
            importVariant(featurePath, selectedVariant)
                .then(module => {
                    // Cache the component
                    const Component = module.default || module;
                    variantCache[featurePath][selectedVariant] = Component;
                    setVariantComponent(() => Component);
                    setIsLoading(false);
                })
                .catch(err => {
                    logger.error(`Failed to load variant ${selectedVariant} for ${featurePath}:`, err);
                    setError(err);
                    setIsLoading(false);
                });
        }, [props.variant]);  // Remove featurePath dependency as it doesn't change

        // Show loading state
        if (isLoading) {
            return props.fallback || React.createElement('div', { className: "lazy-loading-skeleton" });
        }

        // Show error state
        if (error || !VariantComponent) {
            return React.createElement('div', { className: "variant-error" },
                React.createElement('p', null, 'Failed to load component'),
                process.env.NODE_ENV !== 'production' && error &&
                React.createElement('pre', null, error.message)
            );
        }

        // Render the variant component with props
        return React.createElement(VariantComponent, props);
    };

    // Add display name to fix linting error
    VariantLoader.displayName = `VariantLoader(${featurePath.split('/').pop() || 'Component'})`;

    return VariantLoader;
};

/**
 * Creates a lazy-loaded variant component using React.lazy
 * 
 * @param featurePath Path to the feature (e.g., 'Homepage/Hero')
 * @returns A lazy-loaded component that will render the appropriate variant
 */
export const createLazyVariantComponent = (featurePath: string) => {
    return React.lazy(() => {
        // Determine variant from data-theme attribute
        const bodyTheme = document.body.getAttribute('data-theme');
        const variant = (bodyTheme && variantPaths[bodyTheme]) ?
            bodyTheme as VariantKey : 'default';

        return importVariant(featurePath, variant)
            .then(module => {
                // Return in the format expected by React.lazy
                return { default: module.default || module };
            });
    });
};

/**
 * Utility to preload specific variants
 * 
 * @param featurePath Path to the feature
 * @param variants Array of variants to preload
 */
export const preloadVariants = (featurePath: string, variants: VariantKey[] = ['default']) => {
    variants.forEach(variant => {
        // Create a prefetch link element
        const link = document.createElement('link');
        link.rel = 'prefetch';
        link.href = `/wp-content/themes/fitcopilot/dist/chunks/variant-${variant.toLowerCase()}.js`;
        link.as = 'script';
        document.head.appendChild(link);

        // Also start the actual import (but don't wait for it)
        importVariant(featurePath, variant).catch(() => {
            // Silently catch any errors during preloading
        });
    });
};

export default {
    importVariant,
    createVariantComponent,
    createLazyVariantComponent,
    preloadVariants,
    getComponentVariant
};
